<?php namespace heide\Http\Middleware;use Closure;use Session;use App;class Lang {    /**     * Run the request filter.     *     * @param  \Illuminate\Http\Request  $request     * @param  \Closure  $next     * @return mixed     */    public function handle($request, Closure $next)    {        if (!Session::has('Lang'))        {            Session::put('Lang','en');        }        App::setLocale(Session::get('Lang'));        return $next($request);    }}